import { useState, useEffect } from "react";
import "./Login.css";
export default function Login() {
  const [userNameValue, setUserName] = useState("");
  const [passwordValue, setPassword] = useState("");
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  useEffect(() => {
    document.body.style.backgroundColor = "#080710";
    return () => {
      document.body.style.backgroundColor = ""; // Reset to default when unmounted
    };
  }, []);

  function handleUserName(event) {
    setUserName(event.target.value); // Use event.target.value to get input value
  }

  function handlePassword(event) {
    setPassword(event.target.value); // Use event.target.value to get input value
  }

  function handleSubmit(event) {
    event.preventDefault();
    fetch("http://localhost:5000/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        username: userNameValue,
        password: passwordValue,
      }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        setResult(data);
      })
      .catch((error) => {
        setError(error.message);
      });
  }

  return (
    <>
      <div class="background-login">
        <div class="shape"></div>
        <div class="shape"></div>
      </div>
      <form className="login-form" onSubmit={handleSubmit}>
        <h3>Login</h3>
        <p>
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            name="username"
            value={userNameValue}
            onChange={handleUserName}
          />
        </p>

        <p>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            name="password"
            value={passwordValue}
            onChange={handlePassword}
          />
        </p>

        <button id="login-button" type="submit">
          Login
        </button>
      </form>
    </>
  );
}
