import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import About from "./components/About";
import Cart from "./components/Cart";
import Signup from "./components/Signup";
import HomePage from "./components/HomePage/HomePage";
import Login from "./components/Login";
import AddItem from "./components/AddItem";
function App() {
  const [items, fetchedItems] = useState([]);
  const [cartItems, updateCartItems] = useState([]);
  const [isLoggedIn, setLogginIn] = useState(false);
  const [userIdLogged, setUserId] = useState("");

  function handleLogIn(userId, isLogged) {
    setLogginIn(isLogged);
    setUserId(userId);
  }

  function fetchItems() {
    console.log("yes");
    fetch("http://localhost:5000/fetchItems")
      .then((response) => response.json())
      .then(async (data1) => {
        fetchedItems(data1); // First update with data1

        // Fetch items from the second API after the first fetch completes
        const response = await fetch("http://localhost:5000/db-items");
        const data2 = await response.json();
        fetchedItems((prevItems) => [...prevItems, ...data2]); // Combine data1 and data2
      })
      .catch((error) => console.log(error));
  }
  useEffect(() => {
    fetchItems();
  }, []);
  // const fetchedItems = (newItems) => {
  //   setItems((prevItems) => [...prevItems, ...newItems]);
  // };

  // const fetchItems = async () => {
  //   console.log("fetched");
  //   try {
  //     // Fetch items from the first API
  //     const response1 = await fetch("http://localhost:5000/fetchItems");
  //     const data1 = await response1.json();
  //     fetchedItems(data1);

  //     // Fetch items from the second API
  //     const response2 = await fetch("http://localhost:5000/db-items");
  //     const data2 = await response2.json();
  //     fetchedItems(data2);
  //   } catch (error) {
  //     console.error(error);
  //   }
  // };

  // // Fetch items when the component mounts
  // useEffect(() => {
  //   fetchItems();
  // }, []);

  function handleAddToCart(item) {
    const objectItem = {
      image: item.image,
      price: item.price,
      title: item.title,
      id: item.id,
    };
    // check if the item already exists in the cart
    const itemExists = cartItems.some(
      (cartItem) => cartItem.id === objectItem.id
    );

    // if the item does not exist, add it to the cart
    if (!itemExists) {
      updateCartItems([...cartItems, objectItem]);
    }
  }

  const removeFromCart = (index) => {
    const newCart = cartItems.filter((_, i) => i !== index);
    updateCartItems(newCart);
  };

  return (
    <>
      <Router>
        <Routes>
          <Route
            path="/"
            element={
              <HomePage
                cartItems={cartItems}
                items={items}
                handleAddToCart={handleAddToCart}
                isLogged={isLoggedIn}
                handleLogOut={handleLogIn}
              />
            }
          />
          <Route path="/about" element={<About />} />
          <Route
            path="/cart"
            element={
              <Cart cartItems={cartItems} removeFromCart={removeFromCart} />
            }
          />
          <Route
            path="/Signup"
            element={<Signup setLogin={handleLogIn} isLoggedIn={isLoggedIn} />}
          />
          <Route
            path="/login"
            element={<Login setLogin={handleLogIn} isLoggedIn={isLoggedIn} />}
          />
          <Route
            path="/AddItem"
            element={
              <AddItem
                items={items}
                updateArrayItems={fetchedItems}
                userIdLogged={userIdLogged}
              />
            }
          />
        </Routes>
      </Router>
      {/* <Header amountCart={cartItems.length} />
      <Items items={items} addToCart={handleAddToCart} />
      <About />
      <Cart cartItems={cartItems} removeFromCart={removeFromCart} />
      <Signup /> */}
    </>
  );
}
export default App;
