import { useState } from "react";
import "./Signup.css";
import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
export default function Signup({ isLoggedIn, setLogin }) {
  const [userNameValue, setUserName] = useState("");
  const [passwordValue, setPassword] = useState("");
  const [firstnameValue, setFirstname] = useState("");
  const [lastnameValue, setLastname] = useState("");
  const [result, setResult] = useState(null);
  const [errorMessage, setError] = useState(null);
  const navigate = useNavigate();
  useEffect(() => {
    document.body.style.backgroundColor = "#080710";
    return () => {
      document.body.style.backgroundColor = ""; // Reset to default when unmounted
    };
  }, []);

  function handleUserName(event) {
    setUserName(event.target.value); // Use event.target.value to get input value
  }

  function handlePassword(event) {
    setPassword(event.target.value); // Use event.target.value to get input value
  }

  function handleFirstname(event) {
    setFirstname(event.target.value);
  }
  function handleLastname(event) {
    setLastname(event.target.value);
  }

  function handleSubmit(event) {
    event.preventDefault();
    fetch("http://localhost:5000/add-user", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        username: userNameValue,
        password: passwordValue,
        firstname: firstnameValue,
        lastname: lastnameValue,
      }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        setLogin(data.userId, true);
        setResult(data);
      })
      .catch((error) => {
        setLogin(null, false);
        setError(error.message);
      });
  }

  useEffect(() => {
    if (isLoggedIn) {
      setTimeout(() => {
        navigate("/");
      }, 2000); // 2-second delay before navigation
    }
  }, [isLoggedIn, navigate]);

  return (
    <>
      <div class="background">
        <div class="shape"></div>
        <div class="shape"></div>
      </div>
      <form className="signUp-form" onSubmit={handleSubmit}>
        <h3>Signup now!</h3>
        <p>
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            name="username"
            value={userNameValue}
            onChange={handleUserName}
          />
        </p>

        <p>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            name="password"
            value={passwordValue}
            onChange={handlePassword}
          />
        </p>

        <p>
          <label htmlFor="firstname">First name:</label>
          <input
            type="text"
            id="firstname"
            name="firstname"
            value={firstnameValue}
            onChange={handleFirstname}
          />
        </p>
        <p>
          <label htmlFor="lastname">Last name:</label>
          <input
            type="text"
            id="lastname"
            name="lastname"
            value={lastnameValue}
            onChange={handleLastname}
          />
        </p>

        {isLoggedIn && (
          <p>
            <h2 className="success-login">
              Successfully logged in! Redirecting...
            </h2>
          </p>
        )}

        {errorMessage && (
          <p>
            <h2 className="error-login">Error logging in{errorMessage}</h2>
          </p>
        )}

        <button id="signUp-button" type="submit">
          Sign Up
        </button>
      </form>
      {result && <div>Result: {JSON.stringify(result)}</div>}
      {error && <div>Error: {error}</div>}
    </>
  );
}
