import React, { useState, useEffect } from "react";
import "./Cart.css"; // Import the CSS file
import Cart2 from "./Cart2.jsx";
const Cart = ({ cartItems, removeFromCart }) => {
  const [totalToPay, setTotal] = useState(0);

  function handleTotalPay(price, increase, decrease) {
    if (increase) {
      setTotal((prev) => prev + price);
    }
    if (decrease) {
      setTotal((prev) => prev - price);
    }
  }

  function calculateInitialPrice() {
    let total = 0;
    cartItems.forEach((item) => {
      total += item.price;
    });
    setTotal(total);
  }
  // Use useEffect to calculate initial total price on mount or when cartItems change
  useEffect(() => {
    calculateInitialPrice();
  }, [cartItems]);
  return (
    <>
      {/* <div className="cart-container">
        <h2>Shopping Cart</h2>
        <ul className="cart-list">
          {cartItems.map((item, index) => (
            <li key={item.id} className="cart-item">
              <span>
                {item.title} - ${item.price}
              </span>
              <button
                className="remove-button"
                onClick={() => removeFromCart(index)}
              >
                Remove
              </button>
            </li>
          ))}
        </ul>
        {/* <button
        className="add-button"
        onClick={() => addToCart({ name: "Example Item", price: 10 })}
      >
        Add Example Item
      </button> */}
      {/* </div> */}

      <h1>Total to pay: {totalToPay.toFixed(2)}</h1>
      <div className="cart-items">
        {cartItems.length > 0 ? (
          cartItems.map((cartItem, index) => (
            <Cart2
              key={index}
              cartItem={cartItem}
              removeFromCart={() => removeFromCart(index)}
              handleTotalPay={handleTotalPay}
            />
          ))
        ) : (
          <h1 className="cart-empty">Your shopping cart is empty!</h1>
        )}
      </div>
    </>
  );
};

export default Cart;
