import { useState } from "react";
import "./Signup.css";
import { useEffect } from "react";
export default function Signup() {
  const [userNameValue, setUserName] = useState("");
  const [passwordValue, setPassword] = useState("");
  const [firstnameValue, setFirstname] = useState("");
  const [lastnameValue, setLastname] = useState("");
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    document.body.style.backgroundColor = "#080710";
    return () => {
      document.body.style.backgroundColor = ""; // Reset to default when unmounted
    };
  }, []);

  function handleUserName(event) {
    setUserName(event.target.value); // Use event.target.value to get input value
  }

  function handlePassword(event) {
    setPassword(event.target.value); // Use event.target.value to get input value
  }

  function handleFirstname(event) {
    setFirstname(event.target.firstname);
  }
  function handleLastname(event) {
    setLastname(event.target.lastname);
  }

  function handleSubmit(event) {
    event.preventDefault();
    fetch("http://localhost:5000/add-user", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        username: userNameValue,
        password: passwordValue,
        firstname: firstnameValue,
        lastname: lastnameValue,
      }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        setResult(data);
      })
      .catch((error) => {
        setError(error.message);
      });
  }

  return (
    <>
      <div class="background">
        <div class="shape"></div>
        <div class="shape"></div>
      </div>
      <form className="signUp-form" onSubmit={handleSubmit}>
        <h3>Signup now!</h3>
        <p>
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            name="username"
            value={userNameValue}
            onChange={handleUserName}
          />
        </p>

        <p>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            name="password"
            value={passwordValue}
            onChange={handlePassword}
          />
        </p>

        <p>
          <label htmlFor="firstname">First name:</label>
          <input
            type="text"
            id="firstname"
            name="firstname"
            value={firstnameValue}
            onChange={handleFirstname}
          />
        </p>
        <p>
          <label htmlFor="lastname">Last name:</label>
          <input
            type="text"
            id="lastname"
            name="lastname"
            value={lastnameValue}
            onChange={handleLastname}
          />
        </p>

        <button id="signUp-button" type="submit">
          Sign Up
        </button>
      </form>
      {result && <div>Result: {JSON.stringify(result)}</div>}
      {error && <div>Error: {error}</div>}
    </>
  );
}
