import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Header from "./components/Header";
import Items from "./components/Items";
import About from "./components/About";
import Cart from "./components/Cart";
import Signup from "./components/Signup";
function App() {
  const [items, fetchedItems] = useState([]);
  const [cartItems, updateCartItems] = useState([]);

  function fetchItems() {
    fetch("http://localhost:5000/fetchItems")
      .then((response) => response.json())
      .then((data) => fetchedItems(data))
      .catch((error) => console.log(error));
  }

  function handleAddToCart(item) {
    const objectItem = {
      image: item.image,
      price: item.price,
      title: item.title,
      id: item.id,
    };

    updateCartItems([...cartItems, objectItem]);
  }

  const removeFromCart = (index) => {
    const newCart = cartItems.filter((_, i) => i !== index);
    updateCartItems(newCart);
  };

  useEffect(() => {
    fetchItems();
  }, []);

  return (
    <>
      <Router>
        <Switch>
          <Route exact path="/" component={Home} />
          <Route path="/about" component={About} />
          <Route path="/contact" component={Contact} />
        </Switch>
      </Router>
      <Header amountCart={cartItems.length} />
      <Items items={items} addToCart={handleAddToCart} />
      <About />
      <Cart cartItems={cartItems} removeFromCart={removeFromCart} />
      <Signup />
    </>
  );
}
export default App;
