import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
export default function AddItem({ items, updateArrayItems, userIdLogged }) {
  const [titleValue, setTitle] = useState("");
  const [priceValue, setPrice] = useState("");
  const [categoryValue, setCategory] = useState("");
  const [descriptionValue, setDescription] = useState("");
  const [imageValue, setImage] = useState("");
  const [errorMessage, setError] = useState(null);
  const [result, setResult] = useState(null);

  const navigate = useNavigate();

  useEffect(() => {
    document.body.style.backgroundColor = "#080710";
    return () => {
      document.body.style.backgroundColor = ""; // Reset to default when unmounted
    };
  }, []);

  function handleTitle(event) {
    setTitle(event.target.value);
  }
  function handlePrice(event) {
    setPrice(event.target.value);
  }
  function handleCategory(event) {
    setCategory(event.target.value);
  }
  function handleDescription(event) {
    setDescription(event.target.value);
  }
  function handleImage(event) {
    setImage(event.target.value);
  }

  function handleSubmit(event) {
    event.preventDefault();
    fetch("http://localhost:5000/add-items", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        userCreated: userIdLogged,
        title: titleValue,
        price: priceValue,
        category: categoryValue,
        description: descriptionValue,
        image: imageValue,
      }),
    })
      .then((response) => {
        if (!response.ok) {
          // If the response is not OK, parse the JSON to get the error message
          return response.json().then((errorData) => {
            // Throw an error with the message from the response
            throw new Error(errorData.message || "Unknown error");
          });
        }
        return response.json();
      })
      .then((data) => {
        setResult(data);
        setError(null);
      })
      .catch((error) => {
        setError(error.message);
      });
  }

  return (
    <>
      <div className="container">
        <div class="drops">
          <div class="drop drop-1"></div>
          <div class="drop drop-2"></div>
          <div class="drop drop-3"></div>
          <div class="drop drop-4"></div>
          <div class="drop drop-5"></div>
        </div>
      </div>
      <form className="login-form" onSubmit={handleSubmit}>
        <h3>Login</h3>
        <p>
          <label htmlFor="title">title:</label>
          <input
            type="text"
            id="title"
            name="title"
            value={titleValue}
            onChange={handleTitle}
          />
        </p>

        <p>
          <label htmlFor="price">Price:</label>
          <input
            type="number"
            id="price"
            name="price"
            value={priceValue}
            onChange={handlePrice}
          />
        </p>
        <p>
          <label htmlFor="price">category:</label>
          <input
            type="text"
            id="category"
            name="category"
            value={categoryValue}
            onChange={handleCategory}
          />
        </p>
        <p>
          <label htmlFor="price">description:</label>
          <input
            type="text"
            id="description"
            name="description"
            value={descriptionValue}
            onChange={handleDescription}
          />
        </p>
        <p>
          <label htmlFor="price">image:</label>
          <input
            type="text"
            id="image"
            name="image"
            value={imageValue}
            onChange={handleImage}
          />
        </p>

        {errorMessage && (
          <p>
            <h2 className="error-login">{errorMessage}</h2>
          </p>
        )}

        <button id="login-button" type="submit">
          Add Item
        </button>
      </form>
    </>
  );
}
